import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

function ExpertDetail() {
  const { id } = useParams();
  const [expert, setExpert] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState("");
const [selectedTime, setSelectedTime] = useState("");

const [formData, setFormData] = useState({
  name: "",
  email: "",
  phone: "",
  notes: ""
});

const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchExpert = async () => {
      const res = await axios.get(`http://localhost:5000/api/experts/${id}`);
      setExpert(res.data);
      setLoading(false);
    };
    fetchExpert();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!expert) return <p>Expert not found</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{expert.name}</h2>
      <p>Category: {expert.category}</p>
      <p>Experience: {expert.experience} years</p>
      <p>Rating: {expert.rating}</p>

      <h3>Available Slots</h3>

      {expert.slots.map((slot) => (
        <div key={slot.date}>
          <h4>{slot.date}</h4>
          {slot.times.map((time) => (
            <button key={time} style={{ margin: "5px" }}>
              {time}
            </button>
          ))}
        </div>
      ))}
    </div>
  );
}

export default ExpertDetail;
