import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";

function ExpertDetail() {
  const { id } = useParams();
  const [expert, setExpert] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState("");
const [selectedTime, setSelectedTime] = useState("");
useEffect(() => {
  const socket = io("http://localhost:5000");

  socket.on("slotBooked", (data) => {
    if (data.expertId === expert?._id) {
      setExpert((prev) => ({
        ...prev,
        slots: prev.slots.map((slot) =>
          slot.date === data.date
            ? {
                ...slot,
                times: slot.times.filter(
                  (t) => t !== data.timeSlot
                )
              }
            : slot
        )
      }));
    }
  });

  return () => socket.disconnect();
}, [expert]);
const [formData, setFormData] = useState({
  name: "",
  email: "",
  phone: "",
  notes: ""
});

const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchExpert = async () => {
      const res = await axios.get(`http://localhost:5000/api/experts/${id}`);
      setExpert(res.data);
      setLoading(false);
    };
    fetchExpert();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!expert) return <p>Expert not found</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{expert.name}</h2>
      <p>Category: {expert.category}</p>
      <p>Experience: {expert.experience} years</p>
      <p>Rating: {expert.rating}</p>

      <h3>Available Slots</h3>

      {expert.slots.map((slot) => (
        <div key={slot.date}>
          <h4>{slot.date}</h4>
        {slot.times.map((time) => (
  <button
    key={time}
    style={{ margin: "5px" }}
    onClick={() => {
      setSelectedDate(slot.date);
      setSelectedTime(time);
    }}
  >
    {time}
  </button>
))}
        </div>
      ))}
      {selectedTime && (
  <div style={{ marginTop: "20px", border: "1px solid gray", padding: "10px" }}>
    <h3>Book Slot</h3>
    <p>Date: {selectedDate}</p>
    <p>Time: {selectedTime}</p>

    <input
      type="text"
      placeholder="Name"
      value={formData.name}
      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
      disabled={!formData.name || !formData.email || !formData.phone}
    />
    <br />

    <input
      type="email"
      placeholder="Email"
      value={formData.email}
      onChange={(e) => setFormData({ ...formData, email: e.target.value })}
    />
    <br />

    <input
      type="text"
      placeholder="Phone"
      value={formData.phone}
      onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
    />
    <br />

    <textarea
      placeholder="Notes"
      value={formData.notes}
      onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
    />
    <br />

    <button
      onClick={async () => {
        try {
          await axios.post("http://localhost:5000/api/bookings", {
            expertId: expert._id,
            ...formData,
            date: selectedDate,
            timeSlot: selectedTime
          });
          // Remove booked slot from UI instantly
setExpert((prev) => ({
  ...prev,
  slots: prev.slots.map((slot) =>
    slot.date === selectedDate
      ? {
          ...slot,
          times: slot.times.filter((t) => t !== selectedTime)
        }
      : slot
  )
}));

          setMessage("Booking Successful!");
          setSelectedTime("");
        } catch (err) {
          setMessage("Slot already booked!");
        }
      }}
    >
      Confirm Booking
    </button>

    {message && <p>{message}</p>}
  </div>
)}
    </div>
  );
}

export default ExpertDetail;
