import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
const API = "http://localhost:5000/api/experts";

function ExpertList() {
  const [experts, setExperts] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState("");
const [selectedTime, setSelectedTime] = useState("");
const [formData, setFormData] = useState({
  name: "",
  email: "",
  phone: "",
  notes: ""
});
const [message, setMessage] = useState("");

  const fetchExperts = async () => {
    try {
      setLoading(true);
      const res = await axios.get(API, {
        params: { page, limit: 5, search, category }
      });
      setExperts(res.data.experts);
      setTotalPages(res.data.totalPages);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch experts");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExperts();
  }, [page, search, category]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Expert Listing</h2>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by name"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Filter */}
      <input
        type="text"
        placeholder="Filter by category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />

      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}

     {experts.map((expert) => (
  <div
    key={expert._id}
    style={{
      border: "1px solid gray",
      margin: "10px",
      padding: "10px",
      cursor: "pointer"
    }}
    onClick={() => navigate(`/expert/${expert._id}`)}
  >
    <h3>{expert.name}</h3>
    <p>Category: {expert.category}</p>
    <p>Experience: {expert.experience} years</p>
    <p>Rating: {expert.rating}</p>
  </div>
))}
      {/* Pagination */}
      <div>
        <button disabled={page === 1} onClick={() => setPage(page - 1)}>
          Prev
        </button>
        <span> Page {page} of {totalPages} </span>
        <button disabled={page === totalPages} onClick={() => setPage(page + 1)}>
          Next
        </button>
      </div>
    </div>
  );
}

export default ExpertList;
